import React from 'react';
import Link from 'next/link';
import { actions, GetArticles, Article } from '../../shared/src/client';
import { Section, Theme, Text, Divider, CardRow, Card, Carousel } from '../../components';
import { styles } from '../../utils';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowRight } from '@fortawesome/free-solid-svg-icons';

const img = 'data:image/png;base64,';

function Column({
  title,
  articles
}: {
  title: string
  articles: Article[]
}) {
  const classes = Theme.useStyleCreatorClassNames(styleCreator);
  return (
    <div className={classes.section}>
      <div className={classes.sectionHeader}>
        <div>
          <Text variant='h3'>{title}</Text>
        </div>
        <Link 
          href='/section/opinions/[column]'
          as='/section/opinions/column'
        >
          <a className={classes.moreInLink}>
            <Text variant='h4' style={{fontWeight: 400}} className={classes.moreInLinkText}>
              More in {title}
            </Text>
            <FontAwesomeIcon icon={faArrowRight}/>
          </a>
        </Link>
      </div>

      <CardRow items={articles}>
        {article => article ? (
          <Card 
            title={article.title}
            subtitle='Subheading...'
            image={article.media[0]}
            href='/article/[year]/[month]/[slug]'
            as={'/'+article.slug}
          />
        ) : null}
      </CardRow>
    </div>
  );
}

function Category({ 
  section,
  authors
}: { 
  section: GetArticles,
  authors: string[]
}) {
  const classes = Theme.useStyleCreatorClassNames(styleCreator);

  return (
    <Section className={classes.page}>
      <Text variant='h2'>Opinions</Text>

      <CardRow items={section.items.slice(0,3)}>
        {article => article ? (
          <Card 
            tag='News'
            title={article.title}
            image={article.media[0]}
            href='/article/[year]/[month]/[slug]'
            as={'/'+article.slug}
          />
        ) : null}
      </CardRow>

      <Divider className={classes.divider}/>
      <Text variant='h2'>Our Columnists</Text>
      <Carousel
        data={authors}
        renderItem={(author) => (
          <div className={classes.columnist}>
            <div className={classes.columnistPicture}/>
            <Text className={classes.columnistTitle}>{author}</Text>
          </div>
        )}
        keyExtractor={author => author}
        itemWidth={175}
      />

      <Divider className={classes.divider}/>
      <Column
        title='Column'
        articles={section.items}
      />
    </Section>
  );
}

const styleCreator =  Theme.makeStyleCreator(theme => ({
  page: {
    ...styles.page(theme),
    backgroundColor: theme.colors.background
  },

  divider: {
    margin: theme.spacing(6, 0, 4)
  },
  section: {
    // marginTop: theme.spacing(4),
    // marginBottom: theme.spacing(8)
  },
  sectionHeader: {
    ...styles.flex('row'),
    justifyContent: 'space-between'
  },
  moreInLink: {
    ...styles.flex('row'),
    textDecoration: 'none',
    color: theme.colors.accent,
    alignItems: 'center',
  },
  moreInLinkText: {
    marginRight: theme.spacing(1),
    fontWeight: 600
  },
  hideLink: {
    ...styles.hideLink(),
  },

  columnist: {
    ...styles.flex(),
    alignItems: 'center'
  },
  columnistPicture: {
    ...styles.lockHeight(120),
    ...styles.lockWidth(120),
    background: `url(${img})`,
    ...styles.centerBackgroundImage(),
    borderRadius: '100%',
    marginBottom: theme.spacing(1)
  },
  columnistTitle: {
    ...styles.textCenter(),
    ...styles.lockWidth(175)
  }
}));

Category.getInitialProps = async () => {
  const section = await actions.getArticles({
    category: 'Opinions',
    limit: 20
  });
  let authors: any = {};
  section.items.forEach(article => {
    article.authors.forEach(author => {
      authors[author] = true;
    });
  })
  return { 
    section,
    authors: Object.keys(authors).sort()
  };
};

export default Category;